import { useState } from "react";

import useGetData from "../CustomHook/use-GetData";
import classes from "./MovieList.module.css";

const MovieList = (props) => {
  const [movieArr, setMovieArr] = useState([]);

  let isOriginal = props.type === "Original";

  useGetData(props.getLink, arrayHandle);

  function arrayHandle(e) {
    setMovieArr(e.results);
  }

  function clickHandle(e) {
    const [tam] = movieArr.filter((item) => +item.id === +e.target.id);
    props.click(tam);
  }

  function khung(e) {
    const loai = e.target.parentElement.parentElement.id;
    props.clickParent(loai);
  }

  const list = movieArr.map((item) => {
    // console.log(arr);
    return (
      <img
        key={item.id}
        id={item.id}
        src={`https://image.tmdb.org/t/p/original/${
          isOriginal ? item.poster_path : item.backdrop_path
        }`}
        alt="none"
        width="250px"
        height="auto"
        className={classes.pic}
        onClick={clickHandle}
      />
    );
  });

  return (
    <div id={props.type} onClick={khung}>
      <h2 className={classes.tieude}>{props.type}</h2>
      <div className={classes.cover}>{list}</div>
    </div>
  );
};

export default MovieList;
