import { useState } from "react";

import Banner from "../../components/Banner/Banner";
import ErrorBoundary from "../../components/ErrorBoundary/ErrorBoundary";
import MovieList from "../../components/MovieList/MovieList";
import NavBar from "../../components/NavBar/NavBar";

const API_KEY = "96ec8e5f2348abf53ecf12050ca01501";
const requests = {
  fetchTrending: `/trending/all/week?api_key=${API_KEY}&language=en-US`,
  fetchNetflixOriginals: `/discover/tv?api_key=${API_KEY}&with_network=123`,
  fetchTopRated: `/movie/top_rated?api_key=${API_KEY}&language=en-US`,
  fetchActionMovies: `/discover/movie?api_key=${API_KEY}&with_genres=28`,
  fetchComedyMovies: `/discover/movie?api_key=${API_KEY}&with_genres=35`,
  fetchHorrorMovies: `/discover/movie?api_key=${API_KEY}&with_genres=27`,
  fetchRomanceMovies: `/discover/movie?api_key=${API_KEY}&with_genres=10749`,
  fetchDocumentaries: `/discover/movie?api_key=${API_KEY}&with_genres=99`,
  fetchSearch: `/search/movie?api_key=${API_KEY}&language=en-US`,
};
const api = [
  {
    link: `https://api.themoviedb.org/3${requests.fetchTrending}`,
    typeFilm: "Trending",
  },
  {
    link: `https://api.themoviedb.org/3${requests.fetchNetflixOriginals}`,
    typeFilm: "Netflix Originals",
  },
  {
    link: `https://api.themoviedb.org/3${requests.fetchTopRated}`,
    typeFilm: "Top Rated",
  },
  {
    link: `https://api.themoviedb.org/3${requests.fetchActionMovies}`,
    typeFilm: "Action Movies",
  },
  {
    link: `https://api.themoviedb.org/3${requests.fetchComedyMovies}`,
    typeFilm: "Comedy Movies",
  },
  {
    link: `https://api.themoviedb.org/3${requests.fetchHorrorMovies}`,
    typeFilm: "Horror Movies",
  },
  {
    link: `https://api.themoviedb.org/3${requests.fetchRomanceMovies}`,
    typeFilm: "Romance Movies",
  },
  {
    link: `https://api.themoviedb.org/3${requests.fetchDocumentaries}`,
    typeFilm: "Documentaries",
  },
];

function Browse() {
  const [parentID, setParentID] = useState("");
  const [showData, setShowData] = useState({ id: 0, content: "", show: false });

  const getDetail = (e) => {
    console.log("lay : ", e);
    console.log(+showData.id);
    console.log(+e.id);
    if (+showData.id !== +e.id) {
      setShowData({
        id: e.id,
        content: e,
        show: true,
      });
    } else {
      setShowData({ id: 0, content: "", show: false });
    }
  };

  const getParent = (e) => {
    setParentID(e);
  };

  const list = api.map((item) => {
    return (
      <React.Fragment>
        <MovieList
          getLink={item.link}
          type={item.typeFilm}
          click={getDetail}
          clickParent={getParent}
        />
        {parentID === item.typeFilm && showData.show && (
          <MovieDetail getInf={showData.content} />
        )}
      </React.Fragment>
    );
  });

  return (
    <div className="app">
      <NavBar />
      <Banner />
      <ErrorBoundary>
        <MovieList />
      </ErrorBoundary>
    </div>
  );
}

export default Browse;
