import { useState } from "react";

import useGetData from "../CustomHook/use-GetData";
import classes from "./MovieList.module.css";

const MovieList = (props) => {
  const [movieArr, setMovieArr] = useState([]);

  let isOriginal = props.type === "Original";

  useGetData(props.getLink, arrayHandle);

  function arrayHandle(e) {
    setMovieArr(e.results);
  }

  function clickHandle(e) {
    const [tam] = movieArr.filter((item) => +item.id === +e.target.id);
    props.click(tam);
  }
};

export default MovieList;
