import { useState, Fragment } from "react";

import useGetData from "./use-GetData";
import classes from "./Banner.module.css";

// DUMMY_DATA = {};

const Banner = () => {
  const [url, setUrl] = useState();

  const API_KEY = "96ec8e5f2348abf53ecf12050ca01501";
  const link = `https://api.themoviedb.org/3/discover/tv?api_key=${API_KEY}&with_network=123`;

  useGetData({
    link: link,
    handle: bannerHanddle,
  });

  function bannerHanddle(e) {
    let random = Math.floor(Math.random() * e.results.length - 1); // Chose a random film
    let pic = e.results[random].backdrop_path;
    let bannerBackdrop = `https://image.tmdb.org/t/p/original/${pic}`;
    let bannerName = e.results[random].name;
    let bannerOverview = e.results[random].overview;

    setUrl({
      avatar: bannerBackdrop,
      name: bannerName,
      overview: bannerOverview,
    });
  }

  return (
    <Fragment>
      <div>
        <img
          src="https://www.google.com/url?sa=i&url=https%3A%2F%2Fthuthuatphanmem.vn%2Fanh-dep-tong-hop-nhung-hinh-anh-dep-nhat%2F&psig=AOvVaw2X6-8mHw7ZtocSNK-xdyBN&ust=1664610168853000&source=images&cd=vfe&ved=0CAsQjRxqFwoTCID77p-CvPoCFQAAAAAdAAAAABAE"
          alt="khong thay"
          width="100%"
          height="800px"
        />
        <h1>{url.name}</h1>
        <div>
          <button
            onClick={() => {
              console.log("hi all");
            }}
          >
            Play
          </button>
          <button>My List</button>
          <div>
            <p>{url.overview}</p>
          </div>
        </div>
      </div>
    </Fragment>
  );
};

export default Banner;
