import { useState, useEffect, Fragment } from "react";
import YouTube from "react-youtube";

const API_KEY = "";

const Trailer = (props) => {
  const [status, setStatus] = useState(false);
  const [getTrailer, setGetTrailer] = useState(props.movieDetail.backdrop_path); // Set default state to backdrop

  useEffect(() => {
    const trailer = async () => {
      const response = await fetch(
        `https://api.themoviedb.org/3/movie/${props.movieDetail.id}/videos?api_key=3997fc9014661d7c2ce89c2bbea4b9f8`
      );
    };
  });
};

export default Trailer;
